
public class Bigthreenum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=10,b=30,c=40;
     if(a>b) {
    	 
    	 if(a>c) {
    		 System.out.println("a is big");
    		 
    	 }else {
    		 
    		 System.out.println("b is big");
    		 
    	 }
    	 
     }else {
    	 if(b>c) {
    		 System.out.println("b is big");
    	 }else {
    		 System.out.println("c is big");
    	 }
    	 
     }
     
	}

}
