package programpractice;

public class practice8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++)   //line
		{
			
			for(int j=10;j>i;j--)  // space 
			{
				System.out.print(" ");
			}
			  for(int k=1;k<=i;k++)    //star
			{
				System.out.print("*"); 
			}
				
			   System.out.println();
			
		}

	}

}


